CREATE TABLE `pwa`.`trx_header` (`id` INT NOT NULL AUTO_INCREMENT COMMENT 'Primary key' , `restaurant_id` INT NOT NULL COMMENT 'FK to table mst_restaurant' , `patient_id` INT NOT NULL COMMENT 'FK to table mst_patient' , `status` ENUM('process','cancel','done') NOT NULL , `total_amount` FLOAT NOT NULL , `remark` TEXT NOT NULL , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `deleted_at` TIMESTAMP NULL , PRIMARY KEY (`id`), INDEX (`restaurant_id`), INDEX (`patient_id`)) ENGINE = InnoDB;